<?php

namespace Uptaxi\Controllers\Payment;

use Uptaxi\Controllers\MainController;
use Uptaxi\Classes\Language;
use Uptaxi\Classes\LiqPay;

class Checkout extends MainController
{
	public function main()
	{
		$key_decoded = self::keyDecode();

		if (!$key_decoded)
			return $key_decoded;

		$dbfirm = new \DBfirm($key_decoded->firm_id);
		if (!$dbfirm->checkConnection())
			return self::checkoutError();

		$public_key = $dbfirm->GetRow('SELECT get_opt_s(\'terminal_key\') AS val')['val'];
		$private_key = $dbfirm->GetRow('SELECT get_opt_s(\'secret_key\') AS val')['val'];
		$payment_system = $key_decoded->payment_system;

		if (empty($payment_system) || empty($public_key) || empty($private_key))
			return self::checkoutError();

		if ($payment_system == 'liqpay')
		{
			$liqpay = new LiqPay($public_key, $private_key);
			$html = $liqpay->cnb_form(array(
				'action'           => 'auth',
				'amount'           => 0.01,
				'currency'         => 'UAH',
				'description'      => 'Привязка карты для оплаты поездок',
				'order_id'         => $_GET['key'],
				'version'          => 3,
				'recurringbytoken' => 1, //Without this param card_token not generated
				'server_url'       => 'https://kvs.uptaxi.ru/apim/v1/'.Language::get_current().'/payment/confirm?key='.$_GET['key']
			));

			//Auto submit form
			$html .= '<script type="text/javascript">document.querySelector("form").submit();</script>';

			return $this->response->write($html);
		} else if ($payment_system == 'payme'){
			//Код страницы подключение карт payMe
			$html = '<!DOCTYPE html>
