							<img class="card-type-img card-type-img--amex"/>
							<img class="card-type-img card-type-img--visa"/>
							<img class="card-type-img card-type-img--mastercard"/>
							<img class="card-type-img card-type-img--uzcard"/>
							<img class="card-type-img card-type-img--humo"/>
							<img class="card-type-img card-type-img--genric"/>
						</div>
		
					</div>
					<div class="oferta">
						<p>Продолжая, вы присоединяетесь к <a href="https://cdn.payme.uz/terms/main.html"> условиям оферты</a></p>
					</div>
				</div>
				
				<div class="continue">Отправить</div>
				<div class="code_sms" id="code_sms">
					<p id="text_sms"></p>
					<div id="input_sms_code">
						<label for="">Введите код из sms</label>
						<input id="input_sms_val" data-min="0" data-max="999999" type="text">
					</div>
				</div>
				<div class="continue_sms">Отправить</div>
			</div>
			
			<script type="text/javascript">
			$(function(){
				$("body").on("input", "#input_sms_val", function(){
					var value = this.value.replace(/[^0-9]/g, "");
					if (value < $(this).data("min")) {
						this.value = $(this).data("min");
					} else if (value > $(this).data("max")) {
						this.value = this.value[0]+""+this.value[1]+""+this.value[2]+""+this.value[3]+""+this.value[4]+""+this.value[5];
					} else {
						this.value = value;
						if(this.value.length == 6){
							document.getElementsByClassName("continue_sms")[0].style.display = "block";
						}else{
							document.getElementsByClassName("continue_sms")[0].style.display = "none";
						};
					}
				});
				let ccNumberInput = document.querySelector(".card-number-input"),
				ccNumberPattern = /^\d{0,16}$/g,
				ccNumberSeparator = "  ",
				ccNumberInputOldValue,
				ccNumberInputOldCursor,
				
				ccExpiryInput = document.querySelector(".card-expiry-input"),
				ccExpirySeparator = "/",
				ccExpiryInputOldValue,
				ccExpiryInputOldCursor,
				
				ccCardTypeOldValue = "",
				
				mask = (value, limit, separator) => {
					var output = [];
					for (let i = 0; i < value.length; i++)
					{
						if ( i !== 0 && i % limit === 0)
						{
							output.push(separator);
						}
				
						output.push(value[i]);
					}
				
					return output.join("");
				},
				unmask = (value) => value.replace(/[^\d]/g, ""),
				checkSeparator = (position, interval) => Math.floor(position / (interval + ccNumberSeparator.length)),
				ccNumberInputKeyDownHandler = (e) => {
					let el = e.target;
					ccNumberInputOldValue = el.value;
					ccNumberInputOldCursor = el.selectionEnd;
				},
				ccNumberInputInputHandler = (e) => {
					let el = e.target,
						newValue = unmask(el.value),
						newCursorPosition;
				
					if (newValue.match(ccNumberPattern))
					{
						newValue = mask(newValue, 4, ccNumberSeparator);
				
						newCursorPosition = 
							ccNumberInputOldCursor - checkSeparator(ccNumberInputOldCursor, 4) + 
							checkSeparator(ccNumberInputOldCursor + (newValue.length - ccNumberInputOldValue.length), 4) + 
							(unmask(newValue).length - unmask(ccNumberInputOldValue).length)+ccNumberSeparator.length-1;
				
						el.value = (newValue !== "") ? newValue : "";
					} else {
						el.value = ccNumberInputOldValue;
						newCursorPosition = ccNumberInputOldCursor;
					}
				
					el.setSelectionRange(newCursorPosition, newCursorPosition);
				
					highlightCC(el.value);
				
					if (unmask(newValue).length == 16)
						ccExpiryInput.focus();
				},
				highlightCC = (ccValue) => {
					let ccCardType = "",
							ccCardTypePatterns = {
								amex: /^3/,
								visa: /^4/,
								mastercard: /^5/,
								uzcard: /^8/,
								humo: /^9/,
				
								genric: /(^1|^2|^6|^7|^0)/,
							};
				
					for (const cardType in ccCardTypePatterns)
					{
						if (ccCardTypePatterns[cardType].test(ccValue))
						{
							ccCardType = cardType;
							break;
						}
					}
				
					if (ccCardType !== ccCardTypeOldValue)
					{
						ccCardTypeOldValue = ccCardType;
						$(".card-type-img").slideUp();
						$(`.card-type-img--${ccCardType}`).slideDown();
					}
				},
				ccExpiryInputKeyDownHandler = (e) => {
					let el = e.target;
					ccExpiryInputOldValue = el.value;
					ccExpiryInputOldCursor = el.selectionEnd;
				},
				ccExpiryInputInputHandler = (e) => {
					let el = e.target,
						newValue = el.value;
				
					newValue = unmask(newValue);
				
					if (newValue.length == 0||
						newValue.match(/^(0|1)$/g)||
						newValue.match(/^(0[1-9]|1[012])$/g)||
						newValue.match(/^(0[1-9]|1[012])(2|3)$/g)||
						newValue.match(/^(0[1-9]|1[012])(2\d|3\d)$/g))
					{
						newValue = mask(newValue, 2, ccExpirySeparator);
						el.value = newValue;
					} else {
						el.value = ccExpiryInputOldValue;
					}
				
					if (unmask(el.value).length == 4)
					{
						$(".card-box .card-type").stop();
						$(".card-box .card-type").animate({right:"120px"});
						$(".continue").fadeIn();
						el.blur();
					} else {
						$(".card-box .card-type").stop();
						$(".card-box .card-type").animate({right:"20px"});
						$(".continue").fadeOut();
					}
				};
				
				ccNumberInput.addEventListener("keydown", ccNumberInputKeyDownHandler);
				ccNumberInput.addEventListener("input", ccNumberInputInputHandler);
				
				ccExpiryInput.addEventListener("keydown", ccExpiryInputKeyDownHandler);
				ccExpiryInput.addEventListener("input", ccExpiryInputInputHandler);
				let service_id = 16394;
				let urlForDard = "http://"+window.location.host+"/api/client_mobile/v1/ru/payment/checkout/payme"
				$(".continue").click(function(){
					let data = {
						"service_id": service_id,
						"number": $(".card-box input.card-number-input").val().replace(/ /g, ""),
						"expire": $(".card-box input.card-expiry-input").val().replace(/\//, ""),
						"save": true,
						"phone":GetUrlParameter("phone"),
					};
					let x = btoa(JSON.stringify(data));
					x = x+x[0];
					x= x.slice(1); 
					let formData = new FormData();
					formData.append("card", x);
					postData(urlForDard,formData)
						.then(msg=>{
							if(msg.status == "ok"&& msg.id == 1 &&msg.data.send == true){
								//alert("Сообщение отправлено на номер "+msg.data.number+" Ожидание - "+msg.data.wait);
								let time = Number(msg.data.wait);
								let timer = setInterval(()=>{
									document.getElementById("text_sms").innerHTML = `SMS отправлено на номер <b>${msg.data.number}</b>.<br> Срок действия сообщения <b>${time}</b> сек.`;
									time--;
									if(time<1){clearInterval(timer)};
								},1000);
								
								document.getElementsByClassName("code_sms")[0].style.display = "block";
								document.getElementsByClassName("continue")[0].style.display = "none";
							}else{
								console.log(msg);
								alert("Ошибка! "+msg.message.res.error.message);
							};
						})
					});
				$(".continue_sms").click(()=>{
					let data = {
						"service_id": service_id,
						"number": $("#input_sms_val").val().replace(/ /g, ""),
						"phone":GetUrlParameter("phone"),
					};
					let x = btoa(JSON.stringify(data));
					x = x+x[0];
					x= x.slice(1);
					formData = new FormData();
					formData.append("sms", x);
					postData(urlForDard,formData)
						.then(msg=>{
							console.log("sms", msg);
							if(msg.status == "ok"&& msg.id == 1){
								alert("Спасибо! Ваша карта сохранена!");
								var message = {"action":"close"};
        						webkit.messageHandlers.cordova_iab.postMessage(JSON.stringify(message));
							}else{
								console.log(msg);
								if(msg.status){
									alert("Ошибка! "+ msg.message);
								}else{
									alert("Ошибка! "+ msg.message.res.error.message);
								};
							};
					});
				});
			});
		
			async function postData(url = "", data) {
				const response = await fetch(url, {
					method: "POST",
					mode: "cors",
					cache: "no-cache",
					credentials: "same-origin",
					headers: {
					"Auth":GetUrlParameter("token"),
					},
					redirect: "follow", // manual, *follow, error
					referrerPolicy: "no-referrer", // no-referrer, *client
					body: data // body data type must match "Content-Type" header
				});
				return await response.json(); // parses JSON response into native JavaScript objects
			};
			function GetUrlParameter(sParam)
				{
					var sPageURL = window.location.search.substring(1),
						sURLVariables = sPageURL.split("&"),
						sParameterName,
						i;
		
					for (i = 0; i < sURLVariables.length; i++) {
						sParameterName = sURLVariables[i].split("=");
		
						if (sParameterName[0] === sParam) {
							return sParameterName[1] === undefined ? true : decodeURIComponent(sParameterName[1]);
						}
					}
				};
			</script>
			</body>
		</html>';

			return $this->response->write($html);
		} else if ($payment_system == 'wayforpay')
		{
			$client_email = $dbfirm->GetRow('SELECT colaesce(email, \'\') AS email FROM client phone_number=?', $key_decoded->phone_number)['email'];
			$amount = 0;
			$currency = 'UAH';
			$domain_name = $dbfirm->GetRow('SELECT get_opt_s(\'name_firm\') AS val')['val'];

			$sign_str = $public_key.';'.$domain_name.';'.$_GET['key'].';'.$amount.';'.$currency;
			$html = '';
			$html .= '<form style="display:none" action="https://secure.wayforpay.com/verify" method="post">
				<input name="merchantAccount" value="'.$public_key.'"/>
				<input name="merchantAuthType" value="simpleSignature"/>
				<input name="merchantDomainName" value="'.$domain_name.'"/>
				<input name="merchantSignature" value="'.hash_hmac('md5', $sign_str, $private_key).'"/>
				<input name="apiVersion" value="1"/>
				<input name="serviceUrl" value="https://kvs.uptaxi.ru/apim/v1/'.Language::get_current().'/payment/confirm?key='.$_GET['key'].'"/>
				<input name="orderReference" value="'.$_GET['key'].'"/>
				<input name="amount" value="'.$amount.'"/>
				<input name="currency" value="'.$currency.'"/>
				<input name="clientPhone" value="'.$key_decoded->phone_number.'"/>
				<input name="clientEmail" value="'.$client_email.'"/>
			</form>';

			//Auto submit form
			$html .= '<script type="text/javascript">document.querySelector("form").submit();</script>';

			return $this->response->write($html);
		} else if ($payment_system == 'uniteller')
		{
			$shop_idp = $public_key;
			$order_idp = $_GET['key'];
			$currency = 'KGS';
			$customer_idp = trim(base64_encode(openssl_encrypt($key_decoded->firm_id.' '.$key_decoded->phone_number, 'AES-128-ECB', 'yabteb9vzlomal')), '=');
			$password = $private_key;
			$life_time = 600;
			$signature = strtoupper(md5(md5($shop_idp).'&'.md5($order_idp).'&'.md5($life_time).'&'.md5($customer_idp).'&'.md5($password)));

			$html = '';
			$html .= '<form action="https://wpay.uniteller.ru/pay" method="POST">
				<input type="hidden" name="Shop_IDP" value="'.$shop_idp.'">
				<input type="hidden" name="Order_IDP" value="'.$order_idp.'">
				<input type="hidden" name="Lifetime" value="'.$life_time.'">
				<input type="hidden" name="Customer_IDP" value="'.$customer_idp.'">
				<input type="hidden" name="Signature" value="'.$signature.'">
				<input type="hidden" name="Card_Registration" value="1">
				<input type="hidden" name="URL_RETURN_OK" value="http://'.$_SERVER['HTTP_HOST'].str_replace('payment/checkout', '', $_SERVER['REQUEST_URI']).'payment/confirm?key='.$_GET['key'].'">
				<input type="hidden" name="URL_RETURN_NO" value="http://'.$_SERVER['HTTP_HOST'].str_replace('payment/checkout', '', $_SERVER['REQUEST_URI']).'payment/confirm?key='.$_GET['key'].'">
				<input type="hidden" name="Currency" value="'.$currency.'">
				<input type="submit" value="Зарегистрировать">
			</form>';

			//Auto submit form
			$html .= '<script type="text/javascript">document.querySelector("form").submit();</script>';

			return $this->response->write($html);
		} else if ($payment_system == 'rncb')
		{
			$client_email = $dbfirm->GetRow('SELECT colaesce(email, \'\') AS email FROM client phone_number=?', $key_decoded->phone_number)['email'];
			$name_firm = $dbfirm->GetRow('SELECT get_opt_s(\'name_firm\') AS val')['val'];
			$html = '<!DOCTYPE html>
			<html>
			<head>
				<title>Привязка карты для номера '.$key_decoded->phone_number.'</title>
				<script src="https://widget.cloudpayments.ru/bundles/cloudpayments"></script>
				<script type="text/javascript">
					function onLoad()
					{
						this.pay = function () {
						var widget = new cp.CloudPayments();
							widget.pay("charge",// или "auth"
								{//options
									publicId: "pk_8ca7f1ab06c80b4547c38dd5eb9f1",//id из личного кабинета
									description: "Привязка карты в такси '.$name_firm.'",//назначение
									amount: 1,//сумма
									currency: "RUB",//валюта
									accountId: "'.$client_email.'", //идентификатор плательщика (необязательно)
									skin: "mini",//дизайн виджета (необязательно)
								},
								{
									onSuccess: function (options) { // success
										//действие при успешной оплате
										console.log(["onSuccess", options]);
									},
									onFail: function (reason, options) { // fail
										//действие при неуспешной оплате
										console.log(["onFail", reason, options]);
									},
									onComplete: function (paymentResult, options) { //Вызывается как только виджет получает от api.cloudpayments ответ с результатом транзакции.
										//например вызов вашей аналитики Facebook Pixel
										console.log(["onComplete", paymentResult, options]);
									}
								}
