	public function confirm()
	{
		$key_decoded = self::keyDecode();

		if (!$key_decoded)
			return parent::errSer('An unknown error has occurred');

		$dbfirm = new \DBfirm($key_decoded->firm_id);
		if (!$dbfirm->checkConnection())
			return parent::errSer('Error connecting to the DB', -3);

		/*$data = self::callBackData($dbfirm);

		if (!$data)
			return $data;*/
		/* callBackData [START] */
		$public_key = $dbfirm->GetRow('SELECT get_opt_s(\'terminal_key\') AS val')['val'];
		$private_key = $dbfirm->GetRow('SELECT get_opt_s(\'secret_key\') AS val')['val'];
		$payment_system = $key_decoded->payment_system;

		if ($payment_system == 'liqpay')
		{
			$data = $_POST['data'] ?? null;
			$signature = $_POST['signature'] ?? null;

			if (empty($data) || empty($signature))
				return parent::errCli('Post params data or signature is empty', -4);

			$sign = base64_encode(sha1($private_key.$data.$private_key, 1));

			if ($signature !== $sign)
				return parent::errCli('Param signature is not valid', -4);

			$data = json_decode(base64_decode($data));

			if ($data->public_key !== $public_key)
				return parent::errCli('Wrong public key', -4);

			if ($data->status !== 'success')
				return parent::errCli('Payment failed', -4);
			/* callBackData [END] */

			// $card_mask = trim(chunk_split(str_replace('*', '********', $data->sender_card_mask2), 4, ' ')); //With spaces between 4 symbols
			$card_mask = trim(str_replace('*', '********', $data->sender_card_mask2));

			$check_on_exist = $dbfirm->GetRow('SELECT cpm.id FROM client_payment_method cpm, client c WHERE c.phone_number = ? AND c.id = cpm.client_id AND cpm.card_masked = ?', [$key_decoded->phone_number, $card_mask])['id'];

			if (empty($check_on_exist))
