				$res = $dbfirm->Execute('INSERT INTO client_payment_method (card_masked, rebill_id, client_id) VALUES (?, ?, (SELECT id FROM client WHERE phone_number = ?))', [$card_mask, $data->card_token, $key_decoded->phone_number]);
			} else {
				$res = (false !== $dbfirm->Execute('UPDATE client_payment_method SET rebill_id = ? WHERE id = ?', [$data->card_token, $check_on_exist]));
			}

			return parent::success($res);
