	public function apply()
	{
		$key_decoded = self::keyDecode();

		if (!$key_decoded)
			return parent::errSer('An unknown error has occurred');

		$dbfirm = new \DBfirm($key_decoded->firm_id);
		if (!$dbfirm->checkConnection())
			return parent::errSer('Error connecting to the DB', -3);

		/*$data = self::callBackData($dbfirm);

		if (!$data)
			return $data;*/
		/* callBackData [START] */
		$public_key = $dbfirm->GetRow('SELECT get_opt_s(\'terminal_key\') AS val')['val'];
		$private_key = $dbfirm->GetRow('SELECT get_opt_s(\'secret_key\') AS val')['val'];
		$payment_system = $key_decoded->payment_system;

		if ($payment_system == 'liqpay')
		{
			$data = $_POST['data'] ?? null;
			$signature = $_POST['signature'] ?? null;

			if (empty($data) || empty($signature))
				return parent::errCli('Post params data or signature is empty', -4);

			$sign = base64_encode(sha1($private_key.$data.$private_key, 1));

			if ($signature !== $sign)
				return parent::errCli('Param signature is not valid', -4);

			$data = json_decode(base64_decode($data));

			if ($data->public_key !== $public_key)
				return parent::errCli('Wrong public key', -4);

			if ($data->status !== 'success')
				return parent::errCli('Payment failed', -4);
			/* callBackData [END] */

			$order_id = openssl_decrypt(base64_decode($data->order_id), 'AES-128-ECB', 'yabteb9vzlomal');

			if ($data->status !== 'success' && $data->action !== 'pay')
				return parent::errCli('Payment failed');

			$res = $dbfirm->Execute('SELECT payment_result(?, ?, ?)', [$order_id, 'Оплата прошла успешно', true]);

			return parent::success($res);
		} else if ($payment_system == 'wayforpay')
		{
			$data = json_decode(trim(urldecode(file_get_contents('php://input')),'='));

			$hold = $_GET['hold']??null;
			$cancel = $_GET['cancel']??null;
			$add = $_GET['add']??null;

			$orderReference = $data->orderReference;
			$amount = $data->amount;
			$currency = $data->currency;
			$email = $data->email;
			$authCode = $data->authCode;
			$cardPan = $data->cardPan;
			$cardType = $data->cardType;
			$recToken = $data->recToken;
			$transactionStatus = $data->transactionStatus;
			$reason = $data->reason;
			$reasonCode = $data->reasonCode;

			$merchantSignature = $data->merchantSignature;

			$domain_name = $dbfirm->GetRow('SELECT get_opt_s(\'name_firm\') AS val')['val'];
			$time = time();
			$sign_str = $public_key.';'.$orderReference.';'.$amount.';'.$currency.';'.$authCode.';'.$cardPan.';'.$transactionStatus.';'.$reasonCode;

			if ($merchantSignature != hash_hmac('md5', $sign_str, $private_key))
				return parent::errCli('Post params merchantSignature is empty or not valid', -4);

			$order_id = openssl_decrypt(base64_decode($orderReference), 'AES-128-ECB', 'yabteb9vzlomal');

			if ($add)
				$order_id = substr($order_id, 0, -4);

			$check_order = $dbfirm->GetRow('SELECT CASE WHEN price_vruchnuu IS NOT NULL THEN price_vruchnuu ELSE price_local_taxometr END AS price, selected_payment_method_res, holded_price_res AS holded_res, holded_price, get_opt_i(\'id_firm\') as firm_id, replace(phone_number, \'+\', \'\') AS phone_number FROM orders WHERE id = ?', $order_id);

			if ($check_order['selected_payment_method_res'] !== null)
			{
				$_POST['system_status'] = 'Payment result is already there: '.$check_order['selected_payment_method_res'];

				$sign_str = $orderReference.';accept;'.$time;
				$answer = [
					'orderReference' => $orderReference,
					'status' => 'accept',
					'time' => $time,
					'signature' => hash_hmac('md5', $sign_str, $private_key)
				];

				return parent::customAns($answer);
			}

			if ($hold && !$cancel)
			{
				if ($transactionStatus == 'RefundInProcessing' ||
					$transactionStatus == 'Declined' ||
					$transactionStatus == 'Refunded' ||
					//$transactionStatus == 'Voided' ||
					$transactionStatus == 'Expired')
				{
					$errMessage = 'На карте недостаточно средств для создания заказа';

					$dbfirm->Execute('UPDATE orders SET holded_price_res = false WHERE id = ?', $order_id);
					$dbfirm->Execute('SELECT orders_cancel(?, \'API\', ?)', [$order_id, $errMessage]);

					$dbbirga = new \DBbirga();
					if (!$dbbirga->checkConnection())
						return parent::errSer('Error connecting to the DB', -3);

					$jsrUrl = $dbbirga->GetRow('SELECT (split_part(web_server, \':\', 1)||\':30\'||substring(web_socket_port::varchar FROM 3 FOR 2)) AS jsr FROM firms where id = ?', $check_order['firm_id'])['jsr'];

					file_get_contents('http://'.$jsrUrl.'/jsr?key=dd8a8ecfbb152b1f39e92e1f00326b44&phone=%2B'.$check_order['phone_number'].'&com=showAlert(\''.urlencode($errMessage).'\')');

					$_POST['system_status'] = 'Hold failed';
				}

				if ($transactionStatus == 'Pending' ||
					$transactionStatus == 'InProcessing')
				{
					$_POST['system_status'] = 'Waiting result';
				}

				if ($transactionStatus == 'Approved')
				{
					$dbfirm->Execute('UPDATE orders SET holded_price_res = true WHERE id = ?', $order_id);

					$_POST['system_status'] = 'Payment approved';
				}

				$sign_str = $orderReference.';accept;'.$time;
				$answer = [
					'orderReference' => $orderReference,
					'status' => 'accept',
					'time' => $time,
					'signature' => hash_hmac('md5', $sign_str, $private_key)
				];

				return parent::customAns($answer);
			}


			if ($add)
			{
				if ($transactionStatus == 'RefundInProcessing' ||
					$transactionStatus == 'Declined' ||
					$transactionStatus == 'Refunded' ||
					//$transactionStatus == 'Voided' ||
					$transactionStatus == 'Expired')
				{
					$dbfirm->Execute('SELECT payment_result(?, ?, ?)', [$order_id, 'Частичная оплата картой, ВОЗЬМИТЕ НАЛИЧНЫМИ '.($check_order['price']-$check_order['holded_price']).'грн', false]);

					$dbbirga = new \DBbirga();
					if (!$dbbirga->checkConnection())
						return parent::errSer('Error connecting to the DB', -3);

					$jsrUrl = $dbbirga->GetRow('SELECT (split_part(web_server, \':\', 1)||\':30\'||substring(web_socket_port::varchar FROM 3 FOR 2)) AS jsr FROM firms where id = ?', $check_order['firm_id'])['jsr'];

					$errMessage = 'Частичная оплата картой, ДОПЛАТИТЕ НАЛИЧНЫМИ '.($check_order['price']-$check_order['holded_price']).'грн';

					file_get_contents('http://'.$jsrUrl.'/jsr?key=dd8a8ecfbb152b1f39e92e1f00326b44&phone=%2B'.$check_order['phone_number'].'&com=showAlert(\''.urlencode($errMessage).'\')');

					$_POST['system_status'] = 'Add charge failed';
				}

				if ($transactionStatus == 'Pending' ||
					$transactionStatus == 'InProcessing')
				{
					$_POST['system_status'] = 'Waiting result of add';
				}

				if ($transactionStatus == 'Approved')
				{
					$dbfirm->Execute('SELECT payment_result(?, ?, ?)', [$order_id, 'Оплата по карте прошла успешно, средства зачислены на ваш счет', true]);

					$_POST['system_status'] = 'Payment approved of add';
				}

				$sign_str = $orderReference.';accept;'.$time;
				$answer = [
					'orderReference' => $orderReference,
					'status' => 'accept',
					'time' => $time,
					'signature' => hash_hmac('md5', $sign_str, $private_key)
				];

				return parent::customAns($answer);
			}
		}
	}

	private function keyDecode()
	{
		$key = $_GET['key'] ?? null;

		if (empty($key))
			return self::checkoutError();

		$key_decoded = openssl_decrypt(base64_decode($key), 'AES-128-ECB', 'yabteb9vzlomal');

		//If we can't decode key return error
		if (!$key_decoded)
			return self::checkoutError();

		//Explode key by spaces: 0 - phone, 1 - date of key, 2 - time of key, 3 - id_firm, 4 - service id, 5 - payment_system
		$key_decoded = explode(' ', $key_decoded);

		if (count($key_decoded) !== 6)
			return self::checkoutError();

		$key_decoded = (object)[
			'phone_number' => $key_decoded[0],
			'date' => $key_decoded[1],
			'time' => $key_decoded[2],
			'firm_id' => $key_decoded[3],
			'service_id' => $key_decoded[4],
			'payment_system' => $key_decoded[5]
		];

		//Check date in key
		if (!preg_match('/^\d{4}\-\d{2}\-\d{2}$/', $key_decoded->date))
			return self::checkoutError();

		//Check time in key
		if (!preg_match('/^\d{2}\:\d{2}\:\d{2}$/', $key_decoded->time))
			return self::checkoutError();

		//Check firm_id in key
		if (!preg_match('/^\d+$/', $key_decoded->firm_id))
			return self::checkoutError();

		//Check service_id in key
		if (!preg_match('/^\d+$/', $key_decoded->service_id))
			return self::checkoutError();

		return $key_decoded;
	}

