	public function pin_code()
	{
		$user_auth = parent::cau();

		if (!isset($user_auth->login))
			return $user_auth;

		$pin_code = $_POST['pin_code'] ?? null;

		if (!preg_match('/^\d{2}-\d{2}-\d{2}-\d{2}$/', $pin_code))
			return parent::errCli('Pin code is not valid', -7);

		if (empty($pin_code))
			return parent::errCli('Post params pin_code is empty', -4);

		$dbfirm = new \DBfirm($user_auth->id_firm);
		if (!$dbfirm->checkConnection())
			return parent::errSer('Error connecting to the DB', -3);

		$check = $dbfirm->GetRow('SELECT fn_check_pin_code(?, ?) AS val', [$user_auth->login, $pin_code])['val'];

		switch ($check) {
			case 'allowed':
				return parent::success(true);
				break;
			case 'forbidden':
				return parent::success(false);
				break;
			case 'exceed':
				return parent::errCli(Language::data('payment')['limit_exceeded']);
				break;
			default:
				return parent::errSer(Language::data('global')['unknown_error'].' #PCHUT7LNF1PC');
				break;
		}
	}

