									"wait" => $res['result']["wait"],
									"check_on_exist"=>$check_on_exist,
									"phone"=>$phone,
									"res" =>$resBD,
								];
								return parent::success($sendData);
							}else{
								return parent::errCli(["error"=>"ERROR cards.get_verify_code", "data"=>$data, "res"=>$res], -5);
							};
						}else{
							return parent::errCli(["error"=>"Error geted data cards.get_verify_code", "data"=>$data, "res"=>$res], -4);
						};
					}else{
						return parent::errCli(["error"=>"Error geted token cards.create", "data"=>$data, "res"=>$res], -3);
					};
				}else{
					return parent::errCli(["error"=>"Error geted data cards.create", "data"=>$data, "res"=>$res, "url"=>$url], -2);
				};
				//return parent::success('Card data received successfully');
			};
			if(!empty($_POST["sms"])){
				$sms = $_POST["sms"];
				$sms = $sms[strlen($sms) -1].$sms;
				$sms = substr($sms,0,-1);
				$smsJS = base64_decode($sms);
				$sms = json_decode($smsJS, true);
				$phone =  $sms["phone"];
                $data["method"] = 'cards.verify';
			
				$check_on_exist = $dbfirm->GetRow('SELECT cpm.id FROM client_payment_method cpm, client c WHERE c.phone_number = ? AND c.id = cpm.client_id', [$phone])['id'];
				if(empty($check_on_exist)){
					$res['error']['message'] = 'Ошибка при работе с базой данных';
					return parent::errCli(["error"=>"ERROR get token from DB ", "res"=>$res, "sms"=>$sms], -6);
				}else{
					$token = $dbfirm->GetRow('SELECT rebill_id FROM client_payment_method WHERE id=?',[$check_on_exist])['rebill_id'];
				};
                $data["params"] = ["token"=>$token, "code"=>$sms["number"],"check_on_exist"=>$check_on_exist];
                $res = self::getPayMe($url, $data, $PayId);
                if(gettype($res) == 'array'){
                    if(!empty($res['result']['card']['verify']) && $res['result']['card']['verify'] == true){
						// Верификация карты выполнена
                        return parent::success('Card save received successfully');
                    }else{
						return parent::errCli(["error"=>"ERROR cards.verify", "data"=>$data, "res"=>$res], -5);
                    };
                }else{
					return parent::errCli(["error"=>"Error geted data cards.verify", "data"=>$data, "res"=>$res], -2);
                };
			};
			
		}else{
			return parent::errCli("Not POST");
		};
	}
	private function getPayMe($url,$dataSend, $Auth){
		$headers[] = 'X-Auth: '.$Auth;
		$data_string = json_encode ($dataSend, JSON_UNESCAPED_UNICODE);
		$ch = curl_init();
		curl_setopt($ch, CURLOPT_URL, $url);                                //устанавливаем адрес
		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);                        //Вывод в строку вместо прямого вывода в браузер
		curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);                        //Игнорируем верификацию сертификата
		curl_setopt($ch, CURLOPT_POST, 1);                                  //Указываем, что у нас POST запрос
		curl_setopt($ch, CURLOPT_POSTFIELDS, $data_string);                 //Добавляем переменные
		curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);                     //Добавляем массив заголовков
		$output = curl_exec($ch);                                           //Присваиваем ответ переменной
		curl_close($ch);
		if ($output === FALSE) {                                            //Проверяем что не ошибка
			// Если произошла ошибка! 
			return 'Error: ' . curl_error($ch);
		}else{
			return json_decode($output, true);                              //строку JSON в ассоциативный масиив
		};
	}
	
}